package modelo;

public class Lugar {
	//mesa
	private int claveMesa;
	private int capacidad;
	
	//mesa cafecito
	private int numeroMesa;
	private String Ubicacion;
	private int claveMesa2;
	private int claveCafecito;
	
	//mesa contrato
	private int mesaContrato;
	private String fecha;
	private int folio;
	private int claveMesa3;
	
	public Lugar() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public int getClaveMesa() {
		return claveMesa;
	}
	public void setClaveMesa(int claveMesa) {
		this.claveMesa = claveMesa;
	}
	public int getCapacidad() {
		return capacidad;
	}
	public void setCapacidad(int capacidad) {
		this.capacidad = capacidad;
	}
	public int getNumeroMesa() {
		return numeroMesa;
	}
	public void setNumeroMesa(int numeroMesa) {
		this.numeroMesa = numeroMesa;
	}
	
	public String getUbicacion() {
		return Ubicacion;
	}
	public void setUbicacion(String ubicacion) {
		Ubicacion = ubicacion;
	}
	public int getClaveMesa2() {
		return claveMesa2;
	}
	public void setClaveMesa2(int claveMesa2) {
		this.claveMesa2 = claveMesa2;
	}
	public int getClaveCafecito() {
		return claveCafecito;
	}
	public void setClaveCafecito(int claveCafecito) {
		this.claveCafecito = claveCafecito;
	}
	public int getMesaContrato() {
		return mesaContrato;
	}
	public void setMesaContrato(int mesaContrato) {
		this.mesaContrato = mesaContrato;
	}
	public String getFecha() {
		return fecha;
	}
	public void setFecha(String fecha) {
		this.fecha = fecha;
	}
	public int getFolio() {
		return folio;
	}
	public void setFolio(int folio) {
		this.folio = folio;
	}
	public int getClaveMesa3() {
		return claveMesa3;
	}
	public void setClaveMesa3(int claveMesa3) {
		this.claveMesa3 = claveMesa3;
	}
	
	

	
	
	
}
